Cheats.Cheat:
body {
	counter-reset: level1Header level2Header level3Header;
}

h1 {
	counter-increment: level1Header;
	counter-set: level2Header 0;
}
h1::before {
	content: counter(level1Header) ' ';
}

h2{
	counter-increment: level2Header;
	counter-set: level3Header 0
}
h2::before {
	content: counter(level1Header) '.' counter(level2Header) ' ';
}

h3 {
	counter-increment: level3Header;
}
h3::before {
	content: counter(level1Header) '.' counter(level2Header) '.' counter(level3Header) ' ';
}

/* Alternate colors using nth-child selectors */
.container .cheatExplanationBlock a:nth-child(4n+0) {
	color: rgb(204, 153, 205);
}
.container .cheatExplanationBlock a:nth-child(4n+1) {
	color: rgb(103, 205, 204);
}
.container .cheatExplanationBlock a:nth-child(4n+2) {
	color: rgb(240, 141, 73);
}
.container .cheatExplanationBlock a:nth-child(4n+3) {
	color: rgb(126, 198, 153);
}
