function createInputField(){
	const inputElement = document.createElement('input');

	// Accessibility requirement: Must be able to exit with enter or return key.
	inputElement.addEventListener( 'keypress', function(event) {
		if ( event.key === 'Enter' ) {
			inputElement.blur(); // Cursor is freed from the field
		}
	});

	inputElement.addEventListener('change', function (evt) {
		// What ever your app is supposed to do when input field value changes
	});
}
